print("Part one:", [([(instruction == "noop" and (X.append(X[-1]) or True)) or X.append(X[-1] + int(instruction)) for instruction in open("input.txt", "r").read().replace("addx ", "noop\n").split("\n")], sum([i*X[i-1] for i in list(range(20, 221, 40))]))[1] for X in [[1]]][0])
print("Part two:", [([(instruction == "noop" and (X.append(X[-1]) or True)) or X.append(X[-1] + int(instruction)) for instruction in open("input.txt", "r").read().replace("addx ", "noop\n").split("\n")], "".join([(i % 40 == 0) * "\n" + ("#" * ((i % 40)-1 <= x <= (i % 40)+1) + "." * (not ((i % 40)-1 <= x <= (i % 40)+1))) for i, x in enumerate(X[:-1], start=0)]))[1] for X in [[1]]][0])